import React from "react";
import styled from "@emotion/styled";
import * as theme from "@/commons/styles/theme";
import { Create } from "@/commons/utils/variants";
import { Refresh } from "@/commons/components/icons";

const Variants = Create({
  kind: {
    primary: () => ` 
        color: ${theme.color("primary", 0)};
        background: ${theme.color("primary", 9)};
        &:hover {
          background:${theme.color("primary", 7)};
        }
        &:disabled {
          background:${theme.color("primary", 1)};
          cursor:not-allowed;
        }
        & > svg {
          stroke: ${theme.color("primary", 7)}
        }
      `,
    secondary: () => ` 
      color: ${theme.color("primary", 9)};
      background: transparent;
      border: 1px solid ${theme.color("primary", 9)};
      &:hover {
        background:${theme.color("primary", 7)};
      }
      &:disabled {
        background:${theme.color("primary", 1)};
        cursor:not-allowed;
      }
    `,
  },
  size: {
    large: () => `
        padding:${theme.space(4)} ${theme.space(7)};
        font-size: 16px;
      `,
    small: () => `
        padding:${theme.space(2)} ${theme.space(3)};
        font-size: 16px;
      `,
  },
});

const ButtonStyles = styled.button(
  {
    border: "none",
    outline: "none",
    cursor: "pointer",
    display: "flex",
    borderRadius: theme.radius(1),
  },
  (props) => {
    return Variants.get(props);
  }
);

const Button = (props) => {
  let child = props?.loading ? <Refresh /> : null;
  return (
    <ButtonStyles kind={props?.kind} size={props?.size}>
      {props.children} {child}
    </ButtonStyles>
  );
};

export default Button;
